let section=document.querySelectorAll(".section");function isVisible(e){return e.getBoundingClientRect().top-window.innerHeight<-100||window.innerHeight+window.scrollY>=document.body.offsetHeight}function scanDocument(){document.querySelectorAll(".hidden").forEach((function(e){isVisible(e)&&(e.classList.remove("hidden"),e.querySelector(".inner").classList.add("slide"))}))}function throttle(e,n){var t=Date.now();return function(){t+n-Date.now()<0&&(e(),t=Date.now())}}window.addEventListener("scroll",throttle(scanDocument,50));